using Rubin
using Tests
using Elliptic
using HypergeometricFunctions
using Polylogarithms
using SpecialFunctions

@test integrate((a+b*cos(x)+c*cos(x)^2)^-1*sin(x)^5, x) == :(-1//3*c^-1*cos(x)^3+(1/2)*b*c^-2*cos(x)^2+-1*c^-3*(b^2+-1*c*(a+2c))*cos(x)+c^-4*(b^2+-4*a*c)^-1//2*(b^4+2*c^2*(a+c)^2+-2*c*b^2*(c+2a))*arctanh((b^2+-4*a*c)^-1//2*(b+2*c*cos(x)))+(1/2)*b*c^-4*(b^2+-2*c*(a+c))*log(a+b*cos(x)+c*cos(x)^2))
@test integrate((a+b*cos(x)+c*cos(x)^2)^-1*sin(x)^3, x) == :(c^-1*cos(x)+-1//2*b*c^-2*log(a+b*cos(x)+c*cos(x)^2)+-1*c^-2*(b^2+-4*a*c)^-1//2*(b^2+-2*c*(a+c))*arctanh((b^2+-4*a*c)^-1//2*(b+2*c*cos(x))))
@test integrate((a+b*cos(x)+c*cos(x)^2)^-1*sin(x), x) == :(2*(b^2+-4*a*c)^-1//2*arctanh((b^2+-4*a*c)^-1//2*(b+2*c*cos(x))))
@test integrate((a+b*cos(x)+c*cos(x)^2)^-1*csc(x), x) == :((2a+2b+2c)^-1*log(1+-1*cos(x))+-1*(-2b+2a+2c)^-1*log(1+cos(x))+b*(a+b+c)^-1*(-2b+2a+2c)^-1*log(a+b*cos(x)+c*cos(x)^2)+-1*(b^2+-4*a*c)^-1//2*(a+b+c)^-1*(a+c+-1b)^-1*(b^2+-2*c^2+-2*a*c)*arctanh((b^2+-4*a*c)^-1//2*(b+2*c*cos(x))))
@test integrate((a+b*cos(x)+c*cos(x)^2)^-1*csc(x)^3, x) == :(-1//4*(a+c+-1b)^-2*(a+-2b+3c)*log(1+cos(x))+1//4*(a+b+c)^-2*(a+2b+3c)*log(1+-1*cos(x))+(b^2+-4*a*c)^-1//2*(a^2+c^2+-1*b^2+2*a*c)^-2*(b^4+2*c^2*(a+c)^2+-2*c*b^2*(c+2a))*arctanh((b^2+-4*a*c)^-1//2*(b+2*c*cos(x)))+(a+b+c)^-1*(-2b+2a+2c)^-1*csc(x)^2*(b+-1*(a+c)*cos(x))+-1//2*b*(a^2+c^2+-1*b^2+2*a*c)^-2*(b^2+-2*c*(a+c))*log(a+b*cos(x)+c*cos(x)^2))
@test integrate((a+b*cos(x)+c*cos(x)^2)^-1*sin(x)^2, x) == :(-1*x*c^-1+c^-1*(b+(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*(2b+2*(b^2+-4*a*c)^-1//2*(b^2+-2*c*(a+c)))*arctan((b+(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x))+c^-1*(b+-1*(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*(2b+-2*(b^2+-4*a*c)^-1//2*(b^2+-2*c*(a+c)))*arctan((b+-1*(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x)))
@test integrate((a+b*cos(x)+c*cos(x)^2)^-1*csc(x)^2, x) == :((1+cos(x))^-1*(-2b+2a+2c)^-1*sin(x)+-1*(1+-1*cos(x))^-1*(2a+2b+2c)^-1*sin(x)+-2*b*c*(a+b+c)^-1*(a+c+-1b)^-1*(b+(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*(1+-1*b^-1*(b^2+-4*a*c)^-1//2*(b^2+-2*c*(a+c)))*arctan((b+(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x))+-2*b*c*(a+b+c)^-1*(a+c+-1b)^-1*(b+-1*(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*(1+b^-1*(b^2+-4*a*c)^-1//2*(b^2+-2*c*(a+c)))*arctan((b+-1*(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x)))
@test integrate((-2+cos(x)^2+cos(x))^-1*sin(x), x) == :(-1//3*log(1+-1*cos(x))+1//3*log(2+cos(x)))
@test integrate((4+cos(x)^2+-5*cos(x))^-1*sin(x), x) == :(-1//3*log(4+-1*cos(x))+1//3*log(1+-1*cos(x)))
@test integrate((3+cos(x)^2+-2*cos(x))^-1*sin(x), x) == :((1/2)*2^(1/2)*arctan((1/2)*2^(1/2)*(1+-1*cos(x))))
@test integrate((13+cos(x)^2+-4*cos(x))^-2*sin(x), x) == :(-1//54*arctan(-2//3+1//3*cos(x))+(234+-72*cos(x)+18*cos(x)^2)^-1*(2+-1*cos(x)))
@test integrate((a+b*cos(x)+c*cos(x)^2)^-1*cos(x)^4, x) == :((1/2)*x*c^-1+x*c^-3*(b^2+-1*a*c)+(1/2)*c^-1*cos(x)*sin(x)+-1*b*c^-2*sin(x)+-1*c^-3*(b+(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*(2*b^3+2*(b^2+-4*a*c)^-1//2*(b^4+2*a^2*c^2+-4*a*c*b^2)+-4*a*b*c)*arctan((b+(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x))+-1*c^-3*(b+-1*(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*(2*b^3+-2*(b^2+-4*a*c)^-1//2*(b^4+2*a^2*c^2+-4*a*c*b^2)+-4*a*b*c)*arctan((b+-1*(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x)))
@test integrate((a+b*cos(x)+c*cos(x)^2)^-1*cos(x)^3, x) == :(c^-1*sin(x)+-1*b*x*c^-2+c^-2*(b+(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*(2*b^2+-2*a*c+2*b^3*(b^2+-4*a*c)^-1//2+-6*a*b*c*(b^2+-4*a*c)^-1//2)*arctan((b+(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x))+c^-2*(b+-1*(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*(2*b^2+-2*a*c+-2*b^3*(b^2+-4*a*c)^-1//2+6*a*b*c*(b^2+-4*a*c)^-1//2)*arctan((b+-1*(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x)))
@test integrate((a+b*cos(x)+c*cos(x)^2)^-1*cos(x)^2, x) == :(x*c^-1+-1*c^-1*(b+(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*(2b+2*(b^2+-4*a*c)^-1//2*(b^2+-2*a*c))*arctan((b+(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x))+-1*c^-1*(b+-1*(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*(2b+-2*(b^2+-4*a*c)^-1//2*(b^2+-2*a*c))*arctan((b+-1*(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x)))
@test integrate((a+b*cos(x)+c*cos(x)^2)^-1*cos(x), x) == :((b+(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*(2+2*b*(b^2+-4*a*c)^-1//2)*arctan((b+(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x))+(b+-1*(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*(2+-2*b*(b^2+-4*a*c)^-1//2)*arctan((b+-1*(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x)))
@test integrate((a+b*cos(x)+c*cos(x)^2)^-1, x) == :(-4*c*(b^2+-4*a*c)^-1//2*(b+(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*arctan((b+(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x))+4*c*(b^2+-4*a*c)^-1//2*(b+-1*(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*arctan((b+-1*(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x)))
@test integrate((a+b*cos(x)+c*cos(x)^2)^-1*sec(x), x) == :(a^-1*arctanh(sin(x))+-2*c*a^-1*(b+(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*(1+-1*b*(b^2+-4*a*c)^-1//2)*arctan((b+(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x))+-2*c*a^-1*(b+-1*(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*(1+b*(b^2+-4*a*c)^-1//2)*arctan((b+-1*(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x)))
@test integrate((a+b*cos(x)+c*cos(x)^2)^-1*sec(x)^2, x) == :(a^-1*tan(x)+-1*b*a^-2*arctanh(sin(x))+2*b*c*a^-2*(b+(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*(1+-1*b^-1*(b^2+-4*a*c)^-1//2*(b^2+-2*a*c))*arctan((b+(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x))+2*b*c*a^-2*(b+-1*(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*(1+b^-1*(b^2+-4*a*c)^-1//2*(b^2+-2*a*c))*arctan((b+-1*(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x)))
@test integrate((a+b*cos(x)+c*cos(x)^2)^-1*sec(x)^3, x) == :((1/2)*a^-1*arctanh(sin(x))+a^-3*(b^2+-1*a*c)*arctanh(sin(x))+(1/2)*a^-1*sec(x)*tan(x)+-1*b*a^-2*tan(x)+-2*c*a^-3*(b^2+-4*a*c)^-1//2*(b+-1*(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*(b^3+(b^2+-4*a*c)^(1/2)*(b^2+-1*a*c)+-3*a*b*c)*arctan((b+-1*(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+-1*(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x))+2*c*a^-3*(b^2+-4*a*c)^-1//2*(b+(b^2+-4*a*c)^(1/2)+-2c)^-1//2*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*(b^3+-1*(b^2+-4*a*c)^(1/2)*(b^2+-1*a*c)+-3*a*b*c)*arctan((b+(b^2+-4*a*c)^(1/2)+-2c)^(1/2)*(b+(b^2+-4*a*c)^(1/2)+2c)^-1//2*tan((1/2)*x)))
