using Rubin
using Tests
using Elliptic
using HypergeometricFunctions
using Polylogarithms
using SpecialFunctions

@test integrate(x^m*sinintegral(b*x), x) == :(x^(1+m)*(1+m)^-1*sinintegral(b*x)+(1/2)*b^-1*x^m*(im*b*x)^(-1m)*(1+m)^-1*SpecialFunctions.gamma(1+m,im*b*x)+(1/2)*b^-1*x^m*(-1*im*b*x)^(-1m)*(1+m)^-1*SpecialFunctions.gamma(1+m,-1*im*b*x))
@test integrate(x^3*sinintegral(b*x), x) == :(1//4*x^4*sinintegral(b*x)+3//2*b^-4*sin(b*x)+-3//2*x*b^-3*cos(b*x)+-3//4*b^-2*x^2*sin(b*x)+1//4*b^-1*x^3*cos(b*x))
@test integrate(x^2*sinintegral(b*x), x) == :(-2//3*b^-3*cos(b*x)+1//3*x^3*sinintegral(b*x)+-2//3*x*b^-2*sin(b*x)+1//3*b^-1*x^2*cos(b*x))
@test integrate(x*sinintegral(b*x), x) == :((1/2)*x^2*sinintegral(b*x)+-1//2*b^-2*sin(b*x)+(1/2)*x*b^-1*cos(b*x))
@test integrate(sinintegral(b*x), x) == :(x*sinintegral(b*x)+b^-1*cos(b*x))
@test integrate(x^-2*sinintegral(b*x), x) == :(b*cosintegral(b*x)+-1*x^-1*sin(b*x)+-1*x^-1*sinintegral(b*x))
@test integrate(x^-3*sinintegral(b*x), x) == :(-1//2*x^-2*sinintegral(b*x)+-1//4*b^2*sinintegral(b*x)+-1//4*x^-2*sin(b*x)+-1//4*b*x^-1*cos(b*x))
@test integrate(x^3*sinintegral(b*x)^2, x) == :((1/2)*b^-2*x^2+2*b^-4*sin(b*x)^2+-3//2*b^-4*log(x)+1//4*x^4*sinintegral(b*x)^2+3//2*b^-4*cosintegral(2*b*x)+3*b^-4*sin(b*x)*sinintegral(b*x)+-1//4*b^-2*x^2*sin(b*x)^2+(1/2)*b^-1*x^3*cos(b*x)*sinintegral(b*x)+-1*x*b^-3*cos(b*x)*sin(b*x)+-3*x*b^-3*cos(b*x)*sinintegral(b*x)+-3//2*b^-2*x^2*sin(b*x)*sinintegral(b*x))
@test integrate(x^2*sinintegral(b*x)^2, x) == :(1//3*x^3*sinintegral(b*x)^2+2//3*b^-3*sinintegral(2*b*x)+5//6*x*b^-2+-5//6*b^-3*cos(b*x)*sin(b*x)+-4//3*b^-3*cos(b*x)*sinintegral(b*x)+-1//3*x*b^-2*sin(b*x)^2+-4//3*x*b^-2*sin(b*x)*sinintegral(b*x)+2//3*b^-1*x^2*cos(b*x)*sinintegral(b*x))
@test integrate(x*sinintegral(b*x)^2, x) == :((1/2)*b^-2*log(x)+(1/2)*x^2*sinintegral(b*x)^2+-1//2*b^-2*sin(b*x)^2+-1//2*b^-2*cosintegral(2*b*x)+-1*b^-2*sin(b*x)*sinintegral(b*x)+x*b^-1*cos(b*x)*sinintegral(b*x))
@test integrate(sinintegral(b*x)^2, x) == :(x*sinintegral(b*x)^2+-1*b^-1*sinintegral(2*b*x)+2*b^-1*cos(b*x)*sinintegral(b*x))
@test integrate(x^3*sinintegral(a+b*x), x) == :(1//4*x^4*sinintegral(a+b*x)+3//2*b^-4*sin(a+b*x)+(1/2)*a*b^-4*cos(a+b*x)+-3//2*x*b^-3*cos(a+b*x)+-3//4*b^-2*x^2*sin(a+b*x)+-1//4*a^2*b^-4*sin(a+b*x)+-1//4*a^3*b^-4*cos(a+b*x)+-1//4*a^4*b^-4*sinintegral(a+b*x)+1//4*b^-1*x^3*cos(a+b*x)+(1/2)*a*x*b^-3*sin(a+b*x)+-1//4*a*b^-2*x^2*cos(a+b*x)+1//4*x*a^2*b^-3*cos(a+b*x))
@test integrate(x^2*sinintegral(a+b*x), x) == :(-2//3*b^-3*cos(a+b*x)+1//3*x^3*sinintegral(a+b*x)+-2//3*x*b^-2*sin(a+b*x)+1//3*a*b^-3*sin(a+b*x)+1//3*a^2*b^-3*cos(a+b*x)+1//3*a^3*b^-3*sinintegral(a+b*x)+1//3*b^-1*x^2*cos(a+b*x)+-1//3*a*x*b^-2*cos(a+b*x))
@test integrate(x*sinintegral(a+b*x), x) == :((1/2)*x^2*sinintegral(a+b*x)+-1//2*b^-2*sin(a+b*x)+(1/2)*x*b^-1*cos(a+b*x)+-1//2*a*b^-2*cos(a+b*x)+-1//2*a^2*b^-2*sinintegral(a+b*x))
@test integrate(sinintegral(a+b*x), x) == :(b^-1*cos(a+b*x)+b^-1*(a+b*x)*sinintegral(a+b*x))
@test integrate(x^-2*sinintegral(a+b*x), x) == :(-1*x^-1*sinintegral(a+b*x)+-1*b*a^-1*sinintegral(a+b*x)+b*a^-1*cos(a)*sinintegral(b*x)+b*a^-1*cosintegral(b*x)*sin(a))
@test integrate(x^-3*sinintegral(a+b*x), x) == :(-1//2*x^-2*sinintegral(a+b*x)+(1/2)*a^-2*b^2*sinintegral(a+b*x)+(1/2)*a^-1*b^2*cos(a)*cosintegral(b*x)+-1//2*b*a^-1*x^-1*sin(a+b*x)+-1//2*a^-1*b^2*sin(a)*sinintegral(b*x)+-1//2*a^-2*b^2*cos(a)*sinintegral(b*x)+-1//2*a^-2*b^2*cosintegral(b*x)*sin(a))
@test integrate(x^2*sinintegral(a+b*x)^2, x) == :(-1//12*b^-3*sin(2a+2*b*x)+2//3*x*b^-2+2//3*b^-3*sinintegral(2a+2*b*x)+a*b^-3*cosintegral(2a+2*b*x)+-1*a*b^-3*log(a+b*x)+-1*a^2*b^-3*sinintegral(2a+2*b*x)+-4//3*b^-3*cos(a+b*x)*sinintegral(a+b*x)+-2//3*b^-3*cos(a+b*x)*sin(a+b*x)+-1//3*a*b^-3*cos(2a+2*b*x)+1//6*x*b^-2*cos(2a+2*b*x)+-4//3*x*b^-2*sin(a+b*x)*sinintegral(a+b*x)+1//3*a^2*b^-3*sinintegral(a+b*x)^2*(a+b*x)+1//3*b^-1*x^2*sinintegral(a+b*x)^2*(a+b*x)+2//3*a*b^-3*sin(a+b*x)*sinintegral(a+b*x)+2//3*a^2*b^-3*cos(a+b*x)*sinintegral(a+b*x)+2//3*b^-1*x^2*cos(a+b*x)*sinintegral(a+b*x)+-2//3*a*x*b^-2*cos(a+b*x)*sinintegral(a+b*x)+-1//3*a*x*b^-2*sinintegral(a+b*x)^2*(a+b*x))
@test integrate(x*sinintegral(a+b*x)^2, x) == :((1/2)*b^-2*log(a+b*x)+-1//2*b^-2*cosintegral(2a+2*b*x)+1//4*b^-2*cos(2a+2*b*x)+a*b^-2*sinintegral(2a+2*b*x)+-1*b^-2*sin(a+b*x)*sinintegral(a+b*x)+x*b^-1*cos(a+b*x)*sinintegral(a+b*x)+(1/2)*x*b^-1*sinintegral(a+b*x)^2*(a+b*x)+-1*a*b^-2*cos(a+b*x)*sinintegral(a+b*x)+-1//2*a*b^-2*sinintegral(a+b*x)^2*(a+b*x))
@test integrate(sinintegral(a+b*x)^2, x) == :(-1*b^-1*sinintegral(2a+2*b*x)+b^-1*sinintegral(a+b*x)^2*(a+b*x)+2*b^-1*cos(a+b*x)*sinintegral(a+b*x))
@test integrate(x^2*sinintegral(d*(a+b*log(c*x^n))), x) == :(1//3*x^3*sinintegral(d*(a+b*log(c*x^n)))+-1//6*im*x^3*(c*x^n)^(-3*n^-1)*exp(-3*a*b^-1*n^-1)*expintegralei(b^-1*n^-1*(3+-1*im*b*d*n)*(a+b*log(c*x^n)))+1//6*im*x^3*(c*x^n)^(-3*n^-1)*exp(-3*a*b^-1*n^-1)*expintegralei(b^-1*n^-1*(3+im*b*d*n)*(a+b*log(c*x^n))))
@test integrate(x*sinintegral(d*(a+b*log(c*x^n))), x) == :((1/2)*x^2*sinintegral(d*(a+b*log(c*x^n)))+-1//4*im*x^2*(c*x^n)^(-2*n^-1)*exp(-2*a*b^-1*n^-1)*expintegralei(b^-1*n^-1*(2+-1*im*b*d*n)*(a+b*log(c*x^n)))+1//4*im*x^2*(c*x^n)^(-2*n^-1)*exp(-2*a*b^-1*n^-1)*expintegralei(b^-1*n^-1*(2+im*b*d*n)*(a+b*log(c*x^n))))
