@test integrate(x^-2*sinintegral(d*(a+b*log(c*x^n))), x) == :(-1*x^-1*sinintegral(d*(a+b*log(c*x^n)))+(1/2)*im*x^-1*(c*x^n)^(n^-1)*exp(a*b^-1*n^-1)*expintegralei(-1*b^-1*n^-1*(1+im*b*d*n)*(a+b*log(c*x^n)))+-1//2*im*x^-1*(c*x^n)^(n^-1)*exp(a*b^-1*n^-1)*expintegralei(-1*b^-1*n^-1*(1+-1*im*b*d*n)*(a+b*log(c*x^n))))
@test integrate(x^-3*sinintegral(d*(a+b*log(c*x^n))), x) == :(-1//2*x^-2*sinintegral(d*(a+b*log(c*x^n)))+-1//4*im*x^-2*(c*x^n)^(2*n^-1)*exp(2*a*b^-1*n^-1)*expintegralei(-1*b^-1*n^-1*(2+-1*im*b*d*n)*(a+b*log(c*x^n)))+1//4*im*x^-2*(c*x^n)^(2*n^-1)*exp(2*a*b^-1*n^-1)*expintegralei(-1*b^-1*n^-1*(2+im*b*d*n)*(a+b*log(c*x^n))))
@test integrate((e*x)^m*sinintegral(d*(a+b*log(c*x^n))), x) == :(e^-1*(e*x)^(1+m)*(1+m)^-1*sinintegral(d*(a+b*log(c*x^n)))+im*x*(c*x^n)^(-1*n^-1*(1+m))*(e*x)^m*(2+2m)^-1*exp(-1*a*b^-1*n^-1*(1+m))*expintegralei(b^-1*n^-1*(a+b*log(c*x^n))*(1+m+im*b*d*n))+-1*im*x*(c*x^n)^(-1*n^-1*(1+m))*(e*x)^m*(2+2m)^-1*exp(-1*a*b^-1*n^-1*(1+m))*expintegralei(b^-1*n^-1*(a+b*log(c*x^n))*(1+m+-1*im*b*d*n)))
@test integrate(x^-3*sin(b*x)*sinintegral(b*x), x) == :(b^2*cosintegral(2*b*x)+-1//4*b^2*sinintegral(b*x)^2+-1//4*x^-2*sin(b*x)^2+-1//2*x^-2*sin(b*x)*sinintegral(b*x)+-1//4*b*x^-1*sin(2*b*x)+-1//2*b*x^-1*cos(b*x)*sin(b*x)+-1//2*b*x^-1*cos(b*x)*sinintegral(b*x))
