@test integrate(x^-2*cos(b*x)*sinintegral(b*x), x) == :(b*cosintegral(2*b*x)+-1//2*b*sinintegral(b*x)^2+-1//2*x^-1*sin(2*b*x)+-1*x^-1*cos(b*x)*sinintegral(b*x))
