@test integrate(x^3*cos(b*x)*sinintegral(b*x), x) == :(3*b^-4*sinintegral(2*b*x)+4*x*b^-3+-1//6*b^-1*x^3+-6*b^-4*cos(b*x)*sinintegral(b*x)+-4*b^-4*cos(b*x)*sin(b*x)+-2*x*b^-3*sin(b*x)^2+b^-1*x^3*sin(b*x)*sinintegral(b*x)+(1/2)*b^-2*x^2*cos(b*x)*sin(b*x)+-6*x*b^-3*sin(b*x)*sinintegral(b*x)+3*b^-2*x^2*cos(b*x)*sinintegral(b*x))
